package ch_03_operator;

public class MyOper_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* 증감연산자
		  단독으로 사용을 할 때는 전위나 후위나 값이 동일
		 */
		
		int i = 5;
		i++;	//i = i + 1;과 같은 의미.
		System.out.println(i);
		
		i = 5;
		++i;
		System.out.println(i);
		

	}

}
