package ch_04_control;

public class If_exam01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 중괄호 생략하지 말고 사용해야
		
		int a = 3;
		
		if (a > 3) { // if(조건식)
			// 조건식이 참일 때 실행
			System.out.println("a는 2보다 큽니다.");
		} //if 구문의 끝
		
		System.out.println("검사가 끝났습니다.");
		// 조건식과 상관없이 실행

	}

}
