package ch_04_control;

public class While_exam05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// do while = 일단 한번 문장을 수행하고 그 다음 조건식을 검사해 반복을 결정
		// 사용을 권장하진 않음
		int num = 100;
		do {
			System.out.println("적어도 한 번은 출력되는 문장");
		} while(num < 10);

	}

}
